import { HeaderTitles } from './../../interfaces/headerInterface';
import { Component, Input } from '@angular/core';
@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  @Input() pageHeader : HeaderTitles = {
    section:'',
    title:'',
    caption: ''
  }
  @Input() userName = ''
}
<div class="pi-header d-flex justify-content-start">
  <h1 class="pi-mainTitle">Panel de usuario</h1>
  <h2 class="pi-mainSubtitle align-self-end ps-3 mb-3">Bienvenido {{userName}}</h2>
</div>
